    SELECT '.'||column1||'.' as row,
           row_number() over() as row_id
      FROM vals
),
seats as (
    SELECT unnest(regexp_split_to_array(row,'')) as seat,
           row_id
      FROM rows
)
    SELECT seat,
           row_id,
           row_number() over(PARTITION BY row_id) as seat_id
INTO TEMPORARY TABLE all_seats
      FROM seats;

WITH RECURSIVE seats AS(
    SELECT seat,
           '-' as old,
           row_id,
           seat_id,
           1 as counter,
           0 as occupied
      FROM all_seats

      UNION

      -- computing is made one round over 2
      -- first we compute seat flip
      -- second we compute neigbours
     SELECT CASE
              WHEN s.counter%2=0 then s.seat
              WHEN s.seat='L' AND s.occupied=0 THEN '#'
              WHEN s.seat='#' AND s.occupied>=4 THEN 'L'
              ELSE s.seat
            END as seat,
            s.seat as old,
            s.row_id,
            s.seat_id,
            s.counter+1 as counter,
             (CASE WHEN LAG(seat, 99) OVER(ORDER BY row_id,seat_id)='#' THEN 1 ELSE 0 END)
            +(CASE WHEN LAG(seat,100) OVER(ORDER BY row_id,seat_id)='#' THEN 1 ELSE 0 END)
            +(CASE WHEN LAG(seat,101) OVER(ORDER BY row_id,seat_id)='#' THEN 1 ELSE 0 END)
            +(CASE WHEN LAG(seat,1) OVER(ORDER BY row_id,seat_id)='#' THEN 1 ELSE 0 END)
            +(CASE WHEN LAG(seat,1) OVER(ORDER BY row_id,seat_id DESC)='#' THEN 1 ELSE 0 END)
            +(CASE WHEN LAG(seat, 99) OVER(ORDER BY row_id DESC,seat_id)='#' THEN 1 ELSE 0 END)
            +(CASE WHEN LAG(seat,100) OVER(ORDER BY row_id DESC,seat_id)='#' THEN 1 ELSE 0 END)
            +(CASE WHEN LAG(seat,101) OVER(ORDER BY row_id DESC,seat_id)='#' THEN 1 ELSE 0 END)
            as occupied
       FROM seats s
      WHERE counter<200

)
select count(*) as nbr,counter from seats where seat='#' group by 2 order by 2 desc limit 1;

