 WITH RECURSIVE recursive_squaring AS (
     SELECT used_ones,
            ARRAY[used_oriented_ones] as used_oriented_ones,
            down,
            1 as iter,
            line_start as l1_start,
            line_end as l1_end,
            line_start as lf_start,
            line_end as lf_end
       FROM tile_lines

      UNION ALL

     SELECT rs.used_ones||tl.used_ones,
            rs.used_oriented_ones||array[tl.used_oriented_ones],
            tl.down,
            iter+1,
            rs.l1_start,
            rs.l1_end,
            tl.line_start,
            tl.line_end
       FROM recursive_squaring rs
       JOIN tile_lines tl ON rs.down=tl.up
       CROSS JOIN square_size s
      WHERE NOT rs.used_ones && tl.used_ones
      AND iter<=s.size
),
ex1_solution as (
     SELECT row_number() over() as combination_id,
            used_oriented_ones as therow
       FROM recursive_squaring
 CROSS JOIN square_size s
      WHERE iter=s.size
),
solution_explode AS (
    SELECT combination_id,
           therow,
           therow[1][1] as the_line,
           1 as line_id
      FROM ex1_solution

     UNION ALL

    SELECT se.combination_id,
           se.therow,
           therow[line_id+1][1],
           line_id+1
      FROM solution_explode se
CROSS JOIN square_size s
     WHERE line_id<s.size
),
solution_explode_all AS (
    SELECT combination_id,
           therow,
           the_line as item,
           line_id,
           1 as col_id
      FROM solution_explode

     UNION ALL

    SELECT combination_id,
           therow,
           therow[line_id][col_id+1],
           line_id,
           col_id+1
      FROM solution_explode_all
CROSS JOIN square_size s
     WHERE col_id<s.size
),
pictures AS (
