WITH RECURSIVE vals AS(VALUES('')),

parsing AS (
    SELECT substring(column1, g, 1) as c
          ,g
      FROM vals, generate_series(1, length(column1)) as g
  ORDER BY g
),
p2 AS (
SELECT g
      ,c
      ,lag(c, 1) OVER (ORDER BY g) as c1
      ,lag(c, 2) OVER (ORDER BY g) as c2
      ,lag(c, 3) OVER (ORDER BY g) as c3

  from parsing
)
select min(g) from p2 where c!=c1 and c!=c2 and c!=c3 and c1!=c2 and c1!=c3 and c2!=c3 and g>3;
