WITH vals AS(VALUES('')),

parsing AS (
    SELECT substring(column1, g, 1) as c
          ,g
      FROM vals, generate_series(1, length(column1)) as g
  ORDER BY g
),
p2 AS (
SELECT p1.g
      ,p2.arr
  from parsing p1
  JOIN LATERAL (SELECT array_agg(distinct(p.c)) as arr
                  FROM parsing p 
                 where p1.g-14<p.g 
                   and p1.g>=p.g
               ) as p2 on true
)
select min(g)
  from p2
 WHERE array_length(p2.arr, 1)=14

